$(function(){$(".teaser").each(function(){var a=$(this),b=a.html(),c=a.attr("data-teaser-length")||1,d=a.html().split(/([\.\?\!])\s/,c*2).map(function(a,b){return b%2==0?a:a+" "}).join("");b!=d&&(a.html(""),a.append("<span class='teaser-long'>"+b+"</span>"+'<span class="teaser-short">'+d+"<span class='teaser-see-more text-info' style='cursor:pointer;margin-top:5px;display:block;' "+"onclick='$(this).parent().hide();$(this).parent().siblings(\".teaser-long\").show();'> See more...</span>"+"</span>"),a.children(".teaser-long").hide())}),$(".teaser-see-more").mouseenter(function(){$(this).css("text-decoration","underline")}).mouseleave(function(){$(this).css("text-decoration","none")})});